import { useEffect, useState } from "react";
import "./App.css";
import { getMovieList, searchMovie } from "./Api";

const App = () =>  {
  const [popularMovies, setPopularMovies] = useState([])
  useEffect(() => {
    setPopularMovies(getMovieList())
  },[])
  const search = (q) => {
    console.log({q})
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>REACT MOVIE APP</h1>
        <input className="movie-search" placeholder="Cari Film Kesayangan ..." onChange={({target}) => search(target.value)}/>
        <div className="movie-container">
          <div className="movie-wrapper">
            <div className="movie-title">CONTOH 1</div>
            <img className="movie-image" />
            <div className="movie-date">02-08-1998</div>
            <div className="movie-rate">9.8</div>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
